import React, {useState, useEffect} from "react";
import "./Support.scss";
import { Link } from "react-router-dom";


class Support extends React.Component{

    state = {
        dimensions : {
            winWidth : 0,
            winHeight : 0
        },

        email : ""
    }

    componentDidMount(){

        this.cssAnimation = document.createElement('style');
        this.cssAnimation.type = 'text/css';
        this.rules = document.createTextNode('@-webkit-keyframes slider {'+
        'from { left:8%; }'+
        `to { left:${(this.state.dimensions.winWidth*92/100) - 489}px; }`+
        '}');
        this.cssAnimation.appendChild(this.rules);
        document.getElementsByTagName("head")[0].appendChild(this.cssAnimation);

        this.setState({dimensions: {winWidth: window.innerWidth, winHeight: window.innerHeight}})

        window.addEventListener("resize", this.detectSize);

        return () => {
            window.removeEventListener("resize", this.detectSize)
        }

    }

    detectSize = () => {
        this.setState({dimensions: {winWidth: window.innerWidth, winHeight: window.innerHeight}})
    }

    render(){
        return (
          <div className="container support">
              <form>
              </form>
              <div className="text-zone">
                  <h1>Takımımız için yeni</h1>
                  <h1>sponsorlar arıyoruz!</h1>
                  <Link to="/sponsor" className="flat-button">Sponsorluk Dosyası</Link>
              </div>
          </div>
        );  
    }
    


}

export default Support;
